mapper.select:
SELECT USER_MEMBER
FROM member_tb

    USER_MEMBER = #{loginId} and PASSWORD_MEMBER = #{loginPw}

SELECT *
FROM(
        SELECT *
        FROM (
                 SELECT @rownum := @rownum + 1 as row_num, member_tb.*
                 FROM member_tb , (SELECT @rownum := 0) r
             ) a
        WHERE row_num >= 1
    ) b
WHERE row_num <= 10;
